const token =
  $node["Token Normalization "]?.json?.token_clean ||
  $node["Token Normalization "]?.json?.token ||
  $json?.query?.token ||
  "";

const auth = $node["Validate Token + Refresh Expiry1"]?.json || {};
const name = auth.name || "";
const email = auth.email || "";
const role = auth.role || "";

const decisionBaseUrl =
  `http://localhost:5678/webhook/expenseflow/supervisor/decision?token=${encodeURIComponent(token)}`;

const rows = $node["Load Assigned Claims"]?.json?.claims || [];

const dashboardUrl =
  `http://localhost:5678/webhook/app/expenseflow/supervisor/dashboard?token=${encodeURIComponent(token)}`;

const loginUrl =
  `http://localhost:5678/webhook/app/expenseflow/login`;

const fmtDate = (v) => {
  if (!v) return "";
  return String(v).replace("T", " ").replace("Z", "");
};

const badgeClass = (s) => {
  if (s === "APPROVED") return "approved";
  if (s === "REJECTED" || s === "DECLINED") return "declined";
  return "submitted";
};

const total = rows.length;
const submitted = rows.filter(
  (r) => r.status === "PENDING" || r.status === "SUBMITTED"
).length;
const approved = rows.filter((r) => r.status === "APPROVED").length;
const rejected = rows.filter(
  (r) => r.status === "REJECTED" || r.status === "DECLINED"
).length;

const esc = (x) => {
  const s = (x ?? "").toString();
  return s
    .replace(/&/g, "&amp;")
    .replace(/</g, "&lt;")
    .replace(/>/g, "&gt;")
    .replace(/"/g, "&quot;")
    .replace(/'/g, "&#39;");
};

const css = `
<style>
  :root{
    --bg:#f6f7fb;
    --card:#ffffff;
    --text:#0f172a;
    --muted:#64748b;
    --border:#e7eaf3;
    --shadow:0 10px 30px rgba(15, 23, 42, 0.08);
    --primary:#4f46e5;
    --primary2:#4338ca;
    --chip:#eef2ff;
  }
  *{ box-sizing:border-box; font-family: ui-sans-serif, system-ui, -apple-system, Segoe UI, Roboto, Arial; }
  body{ margin:0; background:var(--bg); color:var(--text); }

  .pageTop{
    max-width: 1320px;
    margin: 18px auto 0;
    padding: 0 18px;
    display:flex;
    align-items:center;
    justify-content:space-between;
    gap:16px;
  }

  .logo { display: flex; align-items: center; }
  .logo svg { height: 42px; }
  .logo text { letter-spacing: 0.5px; }

  .logoutBtn{
    display:inline-flex;
    align-items:center;
    justify-content:center;
    padding: 10px 20px;
    border-radius: 12px;
    border: none;
    border-radius: 5px;
    background: linear-gradient(180deg, var(--primary), var(--primary2));
    color: white;
    cursor: pointer;
    text-decoration:none;
    font-weight: 900;
    font-size: 14px;
    transition: background-color 0.3s ease;
  }


  .wrap{ max-width: 1320px; margin: 48px auto; padding: 0 18px; }
  .card{
    background:var(--card);
    border:1px solid var(--border);
    border-radius: 14px;
    box-shadow: var(--shadow);
    padding: 22px;
  }
  .top{
    display:flex; gap:16px; align-items:flex-start; justify-content:space-between;
    padding-bottom: 14px; border-bottom:1px solid var(--border);
    margin-bottom: 16px;
  }
  .title{ font-size: 26px; font-weight: 800; margin: 0; }
  .subtitle{ margin: 6px 0 0; color:var(--muted); font-size: 14px; }
  .who{ text-align:right; }
  .who .name{ font-weight:800; }
  .who .email{ color:var(--muted); font-size: 13px; margin-top: 4px; }

  .btn{
    display:inline-flex; align-items:center; justify-content:center;
    padding: 11px 14px;
    border-radius: 10px;
    border:1px solid rgba(79,70,229,0.25);
    background: linear-gradient(180deg, var(--primary), var(--primary2));
    color:#fff; text-decoration:none;
    font-weight: 800;
    box-shadow: 0 10px 18px rgba(79,70,229,0.22);
  }

  .grid{
    display:grid; grid-template-columns: repeat(3, 1fr);
    gap: 12px;
    margin: 16px 0 18px;
  }

  .stat{
    border:1px solid var(--border);
    border-radius: 12px;
    padding: 12px;
    background: #fafbff;
  }
  .stat .k{ color:var(--muted); font-size: 12px; }
  .stat .v{ font-size: 20px; font-weight: 900; margin-top: 6px; }

  .tableWrap{ overflow:auto; border:1px solid var(--border); border-radius: 12px; }
  table{ width:100%; border-collapse: collapse; min-width: 980px; }
  th, td{ padding: 12px; border-bottom:1px solid var(--border); font-size: 13px; vertical-align: top; }
  th{ text-align:left; color: var(--muted); font-weight: 800; background:#fbfcff; }

  .badge{
    display:inline-flex;
    padding: 5px 10px;
    border-radius: 999px;
    font-weight: 900;
    font-size: 12px;
    background: var(--chip);
  }
  .badge.approved{ background:#ecfdf5; color:#065f46; }
  .badge.declined{ background:#fef2f2; color:#991b1b; }
  .badge.submitted{ background:#eff6ff; color:#1e3a8a; }

  .note{
    width: 240px;
    padding: 9px 10px;
    border-radius: 10px;
    border:1px solid var(--border);
    font-size: 12px;
    margin-right: 8px;
  }

  .decisionForm{
    display:flex;
    flex-direction:column;
    gap:10px;
  }

  .decisionBtns{
    display:flex;
    gap:10px;
  }

  .aBtn{
    border-radius:10px;
    padding:8px 12px;
    font-weight:900;
    font-size:12px;
    border:1px solid var(--border);
    background:#fff;
    cursor:pointer;
  }

  .aBtn.approve{
    background:#ecfdf5;
    border-color:#a7f3d0;
    color:#065f46;
  }

  .aBtn.reject{
    background:#fef2f2;
    border-color:#fecaca;
    color:#991b1b;
  }

  .muted{ color:var(--muted); }
</style>
`;

const logoHtml = `
<div class="logo">
  <svg width="220" height="50" viewBox="0 0 220 50" xmlns="http://www.w3.org/2000/svg">
    <defs>
      <linearGradient id="flowGradient" x1="0" y1="0" x2="1" y2="1">
        <stop offset="0%" stop-color="#2563EB"/>
        <stop offset="100%" stop-color="#06B6D4"/>
      </linearGradient>
    </defs>
    <circle cx="25" cy="25" r="20" fill="url(#flowGradient)" />
    <path d="M15 25 Q25 10 35 25 T55 25" stroke="white" stroke-width="3" fill="none" />
    <text x="60" y="32" font-family="Segoe UI, Arial, sans-serif" font-size="22" font-weight="600" fill="#1E293B">
      ExpenseFlow
    </text>
  </svg>
</div>
`;

const tableHtml = `
<div class="tableWrap">
<table>
<thead>
<tr>
  <th>Claim ID</th>
  <th>Employee</th>
  <th>Submitted</th>
  <th>Category</th>
  <th>Amount</th>
  <th>Merchant</th>
  <th>Status</th>
  <th>Receipt</th>
  <th>Decision</th>
</tr>
</thead>
<tbody>
${
rows.length === 0
? `<tr><td colspan="9" style="text-align:center;padding:28px">No claims assigned to you yet.</td></tr>`
: rows.map((r) => {
  const isFinal =
    r.status === "APPROVED" ||
    r.status === "REJECTED" ||
    r.status === "DECLINED";

  return `
  <tr>
    <td>${esc(r.claim_id)}</td>
    <td>
      <strong>${esc(r.employee_name || "")}</strong><br/>
      <span class="muted">${esc(r.employee_email || "")}</span>
    </td>
    <td>${fmtDate(r.submitted_at)}</td>
    <td>${esc(r.category)}</td>
    <td>${esc(r.amount)} ${esc(r.currency)}</td>
    <td>${esc(r.merchant)}</td>
    <td><span class="badge ${badgeClass(r.status)}">${esc(r.status)}</span></td>
    <td>${r.receipt_url ? `<a href="${esc(r.receipt_url)}" target="_blank">Open</a>` : "—"}</td>
    <td>
      ${
        isFinal
        ? `
          <strong>${esc(r.status)}</strong>
          <div class="muted">${esc(r.decision_notess || "")}</div>
        `
        : `
        <form method="POST" action="${decisionBaseUrl}" class="decisionForm">
          <input type="hidden" name="claim_id" value="${esc(r.claim_id)}" />

          <textarea
            name="decision_notes"
            class="note"
            placeholder="Optional note for employee…"
          ></textarea>

          <div class="decisionBtns">
            <button class="aBtn approve" type="submit" name="action" value="APPROVED">
              Approve
            </button>
            <button class="aBtn reject" type="submit" name="action" value="REJECTED">
              Decline
            </button>
          </div>
        </form>
        `
      }
    </td>
  </tr>
  `;
}).join("")
}
</tbody>
</table>
</div>
`;

const html = `
<html>
<head>
<meta charset="utf-8"/>
<title>ExpenseFlow — Supervisor Dashboard</title>
${css}
</head>
<body>
  <div class="pageTop">
    ${logoHtml}
    <a class="logoutBtn" href="http://localhost:5678/webhook/expenseflow/auth/logout?token=${encodeURIComponent(token)}">Logout</a>
  </div>

<div class="wrap">
<div class="card">
<div class="top">
  <div>
    <h1 class="title">ExpenseFlow</h1>
    <div class="subtitle">Supervisor dashboard — review and decide on employee claims</div>
  </div>
  <div class="who">
    <div class="name">${esc(name)}</div>
    <div class="email">${esc(email)}</div>
    <div style="margin-top:12px">
      <a class="btn" href="${dashboardUrl}">↻ Refresh</a>
    </div>
  </div>
</div>

<div class="grid">
  <div class="stat"><div class="k">Assigned</div><div class="v">${total}</div></div>
  <div class="stat"><div class="k">Awaiting</div><div class="v">${submitted}</div></div>
  <div class="stat"><div class="k">Approved / Rejected</div><div class="v">${approved} / ${rejected}</div></div>
</div>

${tableHtml}
</div>
</div>
</body>
</html>
`;

return [{ json: { html } }];
